import React from 'react';
import ReactFlow from 'react-flow-renderer';

const MindMap = ({ nodes }) => {
  const defaultEdges = nodes.slice(1).map((node, index) => ({
    id: `e${nodes[0].id}-${node.id}`,
    source: nodes[0].id.toString(),  // root node
    target: node.id.toString(),
    type: 'smoothstep'
  }));

  const reactFlowNodes = nodes.map((node) => ({
    id: node.id.toString(),
    data: { label: node.label },
    position: node.pos || { x: Math.random() * 400, y: Math.random() * 300 },
  }));

  return (
    <div style={{ height: 600, border: '1px solid #ccc', borderRadius: '10px' }}>
      <ReactFlow nodes={reactFlowNodes} edges={defaultEdges} fitView />
    </div>
  );
};

export default MindMap;
