import React from 'react';
import ReactFlow from 'react-flow-renderer';

const MindMap = ({ nodes }) => {
  const elements = nodes.map((node, index) => ({
    id: node.id || `${index}`,
    data: { label: node.label || node.word || node.text || 'Node' },
    position: {
      x: Math.random() * 600, // Random spread on X axis
      y: Math.random() * 400  // Random spread on Y axis
    },
    type: 'default',
  }));

  return (
    <div style={{ height: 500, border: '1px solid lightgray', borderRadius: '8px' }}>
      <ReactFlow elements={elements} />
    </div>
  );
};

export default MindMap;
